e:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-690bca18-3cc0-416b-9728-16cdb0d530d9.mdx"},this),"Add a mode toggle"]},void 0,!0,{fileName:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-690bca18-3cc0-416b-9728-16cdb0d530d9.mdx",lineNumber:44,columnNumber:1},this),(0,d.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:[(0,d.jsxDEV)(e.div,{"data-rehype-pretty-code-title":"","data-language":"tsx","data-theme":"default",children:"src/components/ModeToggle.tsx"},void 0,!1,{fileName:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-690bca18-3cc0-416b-9728-16cdb0d530d9.mdx"},this),(0,d.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__withMeta__:!0,__rawString__:`import * as React from "react"
import { Moon, Sun } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

export function ModeToggle() {
  const [theme, setThemeState] = React.useState<
    "theme-light" | "dark" | "system"
  >("theme-light")

  React.useEffect(() => {
    const isDarkMode = document.documentElement.classList.contains("dark")
    setThemeState(isDarkMode ? "dark" : "theme-light")
  }, [])

  React.useEffect(() => {
    const isDark =
      theme === "dark" ||
      (theme === "system" &&
        window.matchMedia("(prefers-color-scheme: dark)").matches)
    document.documentElement.classList[isDark ? "add" : "remove"]("dark")
  }, [theme])

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="icon">
          <Sun className="h-[1.2rem] w-[1.2rem] rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
          <Moon className="absolute h-[1.2rem] w-[1.2rem] rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
          <span className="sr-only">Toggle theme</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end">
        <DropdownMenuItem onClick={() => setThemeState("theme-light")}>
          Light
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setThemeState("dark")}>
          Dark
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setThemeState("system")}>
          System
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  )
}
